import { useEffect, useState } from 'react';

import { PlantItem } from "components/items";
import { useFetch } from 'utilities/hooks';

import styles from './PlantTable.module.css';

const PlantTable = () => {
    const [plantsList, setPlantsList] = useState([]);

    const { isLoading, apiData, apiError } = useFetch(
        "GET",
        `http://192.168.1.253:3001/plants`,
        {}
    );



    useEffect(() => setPlantsList(apiData.map(plant => 
        <PlantItem key={plant.latinName} latinName={plant.latinName} commonName={plant.commonName} sun={plant.sun} water={plant.water} lastWatered={plant.lastWatered} />
    )), [apiData])

    const plantsList2 = apiData.map(plant => 
        <PlantItem key={plant.latinName} latinName={plant.latinName} commonName={plant.commonName} sun={plant.sun} water={plant.water} lastWatered={plant.lastWatered} />
    );

    console.log(apiData);

    if (isLoading) {
        return <p>Loading...</p>
    }

    if (apiError) {
        return <p>Something went wrong!</p>
    }

    return (
        <div className={styles.table}>
            {plantsList}
        </div>
    )
}

export default PlantTable;
