import { PlantItem } from "components/items";
import { useFetch } from 'utilities/hooks';

import styles from './PlantTable.module.css';

const PlantTable = () => {

    const { isLoading, apiData, apiError } = useFetch(
        "GET",
        `http://192.168.1.253:3001/plants`,
        {}
    );

    const plantsList = apiData.map(plant => 
        <PlantItem latinName={plant.latinName} commonName={plant.commonName} sun={plant.sun} water={plant.water} lastWatered={plant.lastWatered} />
    );

    let newDate = new Date();

    const plantArray = [
        {latinName: 'Sansevieria Patens', commonName: 'Snake Plant', sun: 'Med', water: 'Med', lastWatered: newDate.getUTCDay()},
        {latinName: 'Coffea Arabica', commonName: 'Arabian Coffee', sun: 'Med', water: 'Med', lastWatered: newDate.getUTCDay()},
        {latinName: 'Epipremnum Aureum', commonName: 'Golden Pothos', sun: 'Low', water: 'Med', lastWatered: newDate.getUTCDay()}
    ];

    if (isLoading) {
        return <p>Loading...</p>
    }

    if (apiError) {
        return <p>Something went wrong!</p>
    }

    return (
        <div className={styles.table}>
            <p>{plantsList}</p>
            {/*<PlantItem latinName={plantArray[0].latinName} commonName={plantArray[0].commonName} sun={plantArray[0].sun} water={plantArray[0].water} lastWatered={plantArray[0].lastWatered} /> 
            <PlantItem latinName={plantArray[1].latinName} commonName={plantArray[1].commonName} sun={plantArray[1].sun} water={plantArray[1].water} lastWatered={plantArray[1].lastWatered} />
            <PlantItem latinName={plantArray[2].latinName} commonName={plantArray[2].commonName} sun={plantArray[2].sun} water={plantArray[2].water} lastWatered={plantArray[2].lastWatered} />*/}
        </div>
    )
}

export default PlantTable;
